// @flow 
import * as React from 'react';
import {Button, Card, CardActions, CardContent, CardMedia, TextField, Typography} from "@mui/material";
import {Task} from "./Task";
import {useEffect, useState} from "react";
import agent from "../Data/agent";

export const Column = ({column}) => {
    const [tasks, setTasks] = useState([]);

    useEffect(async () => {
        setTasks(await agent.getTasks(column.id))
    },[])

    const handleDelete = (id) => {
        let newTasks = tasks.filter((task) => task.id !== id)
        setTasks(newTasks)
        agent.deleteTask(column.id, id)
    }

    return (
        <div>
            <Card sx={{ maxWidth: 400, margin: 2, bgcolor: '#a8a69e' }}>
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {column.title}
                    </Typography>
                    {tasks.map(task => (
                        <Task key={task.id} task={task} handleDelete={handleDelete}/>
                    ))}
                    <TextField id="outlined-basic" label="New task" variant="outlined" sx={{bgcolor: 'white'}}/>
                </CardContent>
            </Card>
        </div>
    );
};
