import axios from 'axios';

const api = axios.create({
    baseURL:'http://localhost:5001/api/'
})

const getColumns = () => {
    let data = []
    api.get('projects/1/columns').then(response => data = response.data);
    return data;
}
const getTasks = (columnId) => {
    let data = []
    api.get(`projects/1/columns/${columnId}/cards`).then(response => data = response.data);
    return data;
}

const agent = {
    getColumns,getTasks
}

export default agent;
