import * as React from 'react';
import {Box, ClickAwayListener, Grid, IconButton, Paper, styled, TextField, Typography} from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';
import {useState} from "react";


const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    color: theme.palette.text.secondary,
    textAlign: 'center',
    height: 60,
    lineHeight: '60px',
}));

export const Task = ({task, handleDelete , handleTaskClicked}) => {



    return (
        <div>
            {
                 (
                    <Item elevation={16} sx={{ margin: 2 }} onClick={handleTaskClicked}>
                        <Grid container>
                            <Grid item xs={8}>
                                <div>
                                    {task.title}
                                </div>
                            </Grid>

                            <Grid item xs={4}>
                                <IconButton aria-label="delete" onClick={() => handleDelete(task.id)} >
                                    <DeleteIcon fontSize="small"/>
                                </IconButton>
                            </Grid>
                        </Grid>
                    </Item>
                )

            }

        </div>


    );
};
