import * as React from 'react';
import {Box, Grid, IconButton, Paper, styled} from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';


const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    color: theme.palette.text.secondary,
    textAlign: 'center',
    height: 60,
    lineHeight: '60px',
}));

export const Task = ({task}) => {
    return (

            <Item elevation={16} sx={{ margin: 2 }}>
                <Grid container>
                    <Grid item xs={8}>
                        <Typography>{task.title}</Typography>  
                    </Grid>
                    <Grid item xs={4}>
                        <IconButton aria-label="delete" sx={{paddingRight: 0}}>
                            <DeleteIcon />
                        </IconButton>
                    </Grid>
                </Grid>






            </Item>




    );
};
