import * as React from 'react';
import {Box, Grid, IconButton, Paper, styled} from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';


const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    textAlign: 'center',
    color: theme.palette.text.secondary,
    height: 60,
    lineHeight: '60px',
}));

export const Task = ({task}) => {
    return (

            <Item elevation={16} sx={{ margin: 2 }}>


                        {task.title}

                    <IconButton aria-label="delete" sx={{paddingRight: 0}}>
                        <DeleteIcon />
                    </IconButton>


            </Item>

    );
};
