import * as React from 'react';
import {Box, ClickAwayListener, Grid, IconButton, Paper, styled, Typography} from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';


const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    color: theme.palette.text.secondary,
    textAlign: 'center',
    height: 60,
    lineHeight: '60px',
}));

export const Task = ({task, handleDelete}) => {

    const handleClickAway = () => {
        console.log('clicked outside')
    };

    return (
        <div>


                    <Item elevation={16} sx={{ margin: 2 }}>
                        <Grid container>
                            <ClickAwayListener onClickAway={handleClickAway}>
                            <Grid item xs={8}>
                                {task.title}
                            </Grid>
                            </ClickAwayListener>
                            <Grid item xs={4}>
                                <IconButton aria-label="delete" onClick={() => handleDelete(task.id)} >
                                    <DeleteIcon fontSize="small"/>
                                </IconButton>
                            </Grid>
                        </Grid>
                    </Item>


        </div>


    );
};
